/* Keyframe Animations */
@keyframes fadeIn {
	from {
		opacity: 0;
	}
	to {
		opacity: 1;
	}
}

@keyframes slideUp {
	from {
		opacity: 0;
		transform: translateY(50px);
	}
	to {
		opacity: 1;
		transform: translateY(0);
	}
}

@keyframes slideDown {
	from {
		opacity: 0;
		transform: translateY(-50px);
	}
	to {
		opacity: 1;
		transform: translateY(0);
	}
}

@keyframes slideLeft {
	from {
		opacity: 0;
		transform: translateX(50px);
	}
	to {
		opacity: 1;
		transform: translateX(0);
	}
}

@keyframes slideRight {
	from {
		opacity: 0;
		transform: translateX(-50px);
	}
	to {
		opacity: 1;
		transform: translateX(0);
	}
}

@keyframes zoomIn {
	from {
		opacity: 0;
		transform: scale(0.8);
	}
	to {
		opacity: 1;
		transform: scale(1);
	}
}

@keyframes zoomOut {
	from {
		opacity: 0;
		transform: scale(1.2);
	}
	to {
		opacity: 1;
		transform: scale(1);
	}
}

@keyframes bounceIn {
	0% {
		opacity: 0;
		transform: scale(0.3);
	}
	50% {
		opacity: 1;
		transform: scale(1.05);
	}
	70% {
		transform: scale(0.9);
	}
	100% {
		opacity: 1;
		transform: scale(1);
	}
}

@keyframes flipIn {
	from {
		opacity: 0;
		transform: perspective(400px) rotateY(90deg);
	}
	to {
		opacity: 1;
		transform: perspective(400px) rotateY(0);
	}
}

@keyframes rotateIn {
	from {
		opacity: 0;
		transform: rotate(-200deg);
	}
	to {
		opacity: 1;
		transform: rotate(0);
	}
}

@keyframes swingIn {
	0% {
		opacity: 0;
		transform: rotate(15deg);
	}
	20% {
		opacity: 1;
		transform: rotate(-10deg);
	}
	40% {
		transform: rotate(5deg);
	}
	60% {
		transform: rotate(-5deg);
	}
	80% {
		transform: rotate(0deg);
	}
	100% {
		opacity: 1;
		transform: rotate(0deg);
	}
}

@keyframes rollIn {
	from {
		opacity: 0;
		transform: translateX(-100%) rotate(-120deg);
	}
	to {
		opacity: 1;
		transform: translateX(0) rotate(0);
	}
}

@keyframes lightSpeed {
	from {
		opacity: 0;
		transform: translateX(100%) skewX(-30deg);
