	60% {
		opacity: 1;
		transform: translateX(-20px) skewX(20deg);
	}
	80% {
		transform: translateX(0) skewX(-5deg);
	}
	to {
		opacity: 1;
		transform: translateX(0) skewX(0);
	}
}

@keyframes tada {
	0% {
		opacity: 0;
		transform: scale(1) rotate(0);
