/**
 * The following styles get applied inside the editor only.
 */

.wp-block-telex-animated-section {
	min-height: 100px;
	padding: 20px;
	border: 2px dashed #ddd;
	border-radius: 4px;
	opacity: 1 !important;
	transform: none !important;

	&::before {
		content: "Animated Section";
		display: block;
		font-size: 12px;
		color: #999;
		text-transform: uppercase;
		letter-spacing: 1px;
		margin-bottom: 10px;
		font-weight: 600;
	}
}

/**
 * Use this file for JavaScript code that you want to run in the front-end
 * on posts/pages that contain this block.
 */

document.addEventListener('DOMContentLoaded', function() {
	const animatedSections = document.querySelectorAll('.wp-block-telex-animated-section');

	if (!animatedSections.length) {
		return;
	}

	// Create an Intersection Observer
	const observerOptions = {
		root: null,
		rootMargin: '0px',
		threshold: 0.1
	};

	const observer = new IntersectionObserver(function(entries) {
		entries.forEach(function(entry) {
			if (entry.isIntersecting) {
				// Add the visible class to trigger animation
				entry.target.classList.add('is-visible');
				// Stop observing this element after it's animated
				observer.unobserve(entry.target);
			}
		});
	}, observerOptions);

	// Observe all animated sections
	animatedSections.forEach(function(section) {
		observer.observe(section);
	});
});
